import React from "react";
import promptValues from "../client-data/prompt-values"
import {postPreferences, callBackendAPI} from "../requests/index";
class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
        submitted: false,
        selectedItems: new Map()
    };
    this._renderRadioButtons = this._renderRadioButtons.bind(this);
    this._handleSelect = this._handleSelect.bind(this);
    this._submitPreferences = this._submitPreferences.bind(this);
    this._mapHelper = this._mapHelper.bind(this)
  }

  _mapHelper() {
      let array = []
    for (const [k, v] of this.state.selectedItems) {
        let object = {}
        console.log(k, v)
        object[k] = v
        array.push(object)
      }
      console.log(array, "the array before finishing")
     return array
  }

  _handleSelect(e) {
      const value = e.target.value
      const checked = e.target.checked
      this.setState(prevState => ({
          checkedItems: prevState.selectedItems.set(value, checked)
      }))
      console.log(this.state, "the state after the change")
  }
//   function mapToJson(map) {
//     return JSON.stringify([...map]);
// }
// function jsonToMap(jsonStr) {
//     return new Map(JSON.parse(jsonStr));
// }

  _submitPreferences() {
      const preferencesConverted = this._mapHelper()
      console.log (preferencesConverted, "the try")
    //   console.log(preferences.split())
    let postBody = {
        preferences: preferencesConverted
    }
    console.log(postBody, "The post body")
    Promise.resolve(postPreferences(postBody))
      .then(response => {
          console.log(response)
      })
      .catch (error => {
          console.log(error, "The error")
      })

  }

  _renderRadioButtons() {
      const radioButtons = promptValues.map((prompt, index) => {
          return (
            <div className="prompt" key={index}>
            <p>{prompt.prompt}</p>
              <label>
                <input
                type="radio" 
                value={prompt.valueOne.value}
                onChange={this._handleSelect}
                />
                {prompt.valueOne.label}
              </label>
              <label className="value-two-label">
                <input 
                  type="radio" 
                  value={prompt.valueTwo.value}
                  onChange={this._handleSelect}
                />
                {prompt.valueTwo.label}
              </label>
              </div>
          )
      })
      return radioButtons
  }



  render() {
      const radioButtons = this._renderRadioButtons()
    return (<div className="main-container">
              <h1>Answer the questions to generate a playlist</h1>
                <form>
                    {radioButtons}
                </form>
                <button 
                className="submit-button"
                onClick={this._submitPreferences}
                >Play it!</button>
          </div>)
  }
}
export default Main;
